import React, { useEffect } from 'react'
import Section from '../../SectionTitle/Section'
import SectionTitle from '../../SectionTitle/SectionTitle'
import Card from '../../Card/Card'
import { Link } from 'react-router-dom'
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import styles from './styles.module.scss'
import getApi from '../../../http/getApi'

export const LatestNews = () => {
    useEffect(() => {
        async function fetchData() {
            try {
                const queryString = new URLSearchParams(params).toString();
                const blogs = await getApi.getData(`/blogs?${queryString}`);
                setData(blogs.data); // თუ გაქვს pagination სტრუქტურა, დაარეგულირე
                setTotalPages(blogs.totalPages); // თუ გიგზავნის საერთო გვერდებს
            } catch (err) {
                console.error('Error fetching blogs:', err);
            }
        }

        fetchData();
    }, [params]);
    const data = [
        {
            _id: 'd1dsc',
            cover: 'https://static.tkt.ge/next/static/images/zugdidi.jpg',
            title: 'title',
            desc: 'text',
        },
        {
            _id: '3fea',
            cover: 'https://static.tkt.ge/next/static/images/zugdidi.jpg',
            title: 'title',
            desc: 'text',
        },
        {
            _id: '42dfassd',
            cover: 'https://static.tkt.ge/next/static/images/zugdidi.jpg',
            title: 'title',
            desc: 'text',
        },
        {
            _id: '2ggddvsddf',
            cover: 'https://static.tkt.ge/next/static/images/zugdidi.jpg',
            title: 'title',
            desc: 'text',
        }
    ]
    return (
        <Section>
            <SectionTitle title={'Latest News'} />
            <Swiper
                spaceBetween={25}
                slidesPerView={4}
                onSlideChange={() => console.log('slide change')}
                onSwiper={(swiper) => console.log(swiper)}
            >
                {data && data?.map((item) => {
                    return (
                        <SwiperSlide key={item._id}>
                            <Card
                                cover={item.cover}
                                title={item.title}
                                desc={item.desc}
                                url={'/'}
                            />
                        </SwiperSlide>
                    )
                })}
            </Swiper>
            <div className='d-flex justify-content-center'>
                <Link to={'/news'}>View All</Link>
            </div>
        </Section>
    )
}
